package src.arrays;

public class IntializingArrays {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a1[]; // declaring array
		int[] a2; // declaring array
		a1 = new int[10]; // creating array of 10 elements
		a2 = new int[20]; // creating array of 20 elements
		int a3[] = new int[30]; // declaring and creating array of 30 elements
		int n = 40;
		int a4[] = new int[n]; // creating and declaring with variable value
		int a5[][] = new int[5][5]; // two dimensional array
		int a6[] = { 10, 20, 30 }; // intializing array
		int a7[] = new int[] { 10, 20, 30 }; // intializing array
		int a8[][] = { { 10, 20, 30 }, { 40, 50, 60 } }; // intializing double
															// dimensional array
	}
}
